// A helper component for a single image row
const ImageRow = ({ images, offset }) => (
  <Box
    display="flex"
    justifyContent={offset ? "flex-end" : "flex-start"}
    mb={4}
  >
    <SimpleGrid columns={images.length} spacing={4}>
      {images.map((src, index) => (
        <Image key={index} src={src} boxSize="150px" objectFit="cover" />
      ))}
    </SimpleGrid>
  </Box>
);

// The main Zigzag component
const ZigzagPattern = ({ imageUrls }) => {
  // Determine the chunk sizes for the zigzag pattern
  const chunkSizes = [3, 2, 2];

  // Split the imageUrls array into chunks based on the pattern
  const chunkedImages = [];
  let startIndex = 0;
  while (startIndex < imageUrls.length) {
    for (let size of chunkSizes) {
      if (startIndex >= imageUrls.length) break;
      chunkedImages.push(imageUrls.slice(startIndex, startIndex + size));
      startIndex += size;
    }
  }

  return (
    <Box>
      {chunkedImages.map((images, index) => (
        <ImageRow key={index} images={images} offset={index % 2 !== 0} />
      ))}
    </Box>
  );
};
