import { extendTheme } from "@chakra-ui/react";

const theme = extendTheme({
  colors: {
    brand: "#5887ff",
  },
  fonts: {
    body: "Figtree, sans-serif",
    heading: "LDF-Comic-Sans-Bold, sans-serif",
    display: "Comic-Sans-MS, sans-serif",
  },
});

export default theme;
