import { extendTheme } from "@chakra-ui/react";

const theme = extendTheme({
  colors: {
    brand: "#5887ff",
  },
  fonts: {
    body: "Figtree, sans-serif",
    heading: "Figtree, sans-serif",
    mono: "Figtree, monospace",
  },
});

export default theme;
