import { useState } from "react";

import {
  Icon,
  IconButton,
  Image,
  Input,
  Progress,
  Spinner,
  Text,
  VStack,
} from "@chakra-ui/react";
import { FiCamera } from "react-icons/fi";
import axios from "axios";
import imageCompression from "browser-image-compression";

export const UploadSelfie = () => {
  const [image, setImage] = useState(null); // image file
  const [loading, setLoading] = useState(false); // loading state
  const [url, setUrl] = useState(""); // public url

  // handle change of input
  const handleChange = async (e) => {
    if (e.target.files[0]) {
      const file = e.target.files[0];

      const options = {
        maxSizeMB: 1,
        maxWidthOrHeight: 800,
        useWebWorker: true,
      };

      try {
        setLoading(true);
        const compressedFile = await imageCompression(file, options);
        setImage(compressedFile);
        handleUpload(compressedFile);
      } catch (error) {
        console.error("Error compressing the file:", error);
      }
    }
  };

  // handle upload of file
  const handleUpload = async (file) => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await axios.post("/api/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setLoading(false);
      setUrl(response.data.fileUrl);
    } catch (error) {
      console.error("Error uploading the file:", error);
    }
  };

  return (
    <VStack spacing={5}>
      <Text fontSize="lg">Start by taking a selfie.</Text>
      <Input
        type="file"
        accept="image/*"
        onChange={handleChange}
        display="none"
        id="file-input"
      />
      <label htmlFor="file-input">
        {!loading && (
          <IconButton
            as="span"
            boxSize={24}
            icon={<Icon as={FiCamera} fontSize={30} />}
            isRound
            mb={4}
            _hover={{ cursor: "pointer", bg: "blue.100" }}
          >
            Upload
          </IconButton>
        )}
        {loading && <Spinner size="lg" p={5} color="brand" />}
      </label>
      {url && <Image src={url} alt="Uploaded file" mt={4} />}
    </VStack>
  );
};
